    CLEAR_PROFILE,
    ACCOUNT_DELETED,
    GET_REPOS
} from './types';

//Get current users profile
export const getCurrentProfile=()=> async dispatch=>{
    try {
      const res= await axios.get('/api/profile/me');

      dispatch({
        type: GET_PROFILE,
        payload: res.data
      });
    } catch (err) {
      dispatch({
        type: PROFILE_ERROR,
        payload: { msg: err.response.statusText , status:err.response.status}
      });
    }
};

